import { useEffect, useRef } from 'react'
import { motion, useScroll, useMotionValueEvent, useTransform, useMotionValue, useMotionTemplate } from "motion/react"
import './Lipstick.css'

function Lipstick() {
    const animationWrapper = useRef(null);

    const { scrollYProgress, scrollY } = useScroll({
        target: animationWrapper,
    });

    const left = useTransform(scrollYProgress, [0, 0.2], ["75%", "50%"]);
    const rotate = useTransform(scrollYProgress, [0, 0.2], ["45deg", "0deg"]);
    
    return (
        <>
            <div ref={animationWrapper} className="lipstick-animation-wrapper" style={{ position: "relative", height: "500vh", width: "100vw" }}>
                <motion.img
                    className="lipstick"
                    src="data:image/png;base64,"
                    alt="Lipstick"
                    style={{
                        position: "sticky",
                        top: "25%",
                        left,
                        rotate,
                        transform: "translate(-50%, -50%)",
                    }}
                />
            </div>
            <div style={{ height: "100vh", width: "100vw" }}></div>
        </>
    )
}

export default Lipstick